import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ChatBox from './components/ChatBox';
import './App.css';

function App() {
  const [sessions, setSessions] = useState([]);
  const [activeSession, setActiveSession] = useState(null);
  const [userId, setUserId] = useState('');
  const [initialized, setInitialized] = useState(false);

  useEffect(() => {
    const initializeUserId = async () => {
      if (!initialized) {
        const newUserId = prompt('Enter your user ID:');
        if (newUserId) {
          setUserId(newUserId);
          setInitialized(true); // Mark initialization as done

          try {
            const response = await axios.get(`http://localhost:5500/api/conversations/sessions/${newUserId}`);
            setSessions(response.data);
          } catch (error) {
            console.error('Failed to fetch sessions:', error);
          }
        }
      }
    };

    initializeUserId();
  }, [initialized]);

  const startNewSession = async () => {
    const sessionName = prompt('Enter session name:');
    if (!sessionName) return;

    try {
      const response = await axios.post('http://localhost:5500/api/messages/session', { userId, sessionName });
      setSessions([...sessions, { sessionId: response.data.sessionId, sessionName: response.data.sessionName }]);
      setActiveSession(response.data.sessionId);
    } catch (error) {
      console.error('Failed to start a new session:', error);
    }
  };

  return (
    <div className="App">
      <div className="sidebar">
        <h2>Chat Sessions</h2>
        <button onClick={startNewSession}>New Session</button>
        <ul>
          {sessions.map((session) => (
            <li key={session.sessionId} onClick={() => setActiveSession(session.sessionId)}>
              {session.sessionName}
            </li>
          ))}
        </ul>
      </div>
      <div className="chat-container">
        <header className="chat-header">
          TRAILS ChatBot
        </header>
        {activeSession && <ChatBox sessionId={activeSession} />}
      </div>
    </div>
  );
}

export default App;
