import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MessageForm from './MessageForm';
import './ChatBox.css'; // Import the CSS file

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [sessionId, setSessionId] = useState('');

  useEffect(() => {
    const fetchSessionId = async () => {
      try {
        const response = await axios.post('http://localhost:5500/api/messages/session');
        setSessionId(response.data.sessionId);
      } catch (error) {
        console.error('Failed to fetch session ID:', error);
      }
    };

    fetchSessionId();
  }, []);

  const addMessage = (msg) => {
    setMessages([...messages, msg]);
  };

  return (
    <div className="chat-box">
      {messages.map((msg, index) => (
        <div key={index} className={`message ${msg.sender}`}>
          {msg.sender === 'user' ? 'You' : 'Bot'}: {msg.content}
        </div>
      ))}
      <MessageForm onSendMessage={addMessage} sessionId={sessionId} />
    </div>
  );
};

export default ChatBox;
