import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ChatBox from './components/ChatBox';
import './App.css';

function App() {
  const [sessions, setSessions] = useState([]);
  const [activeSession, setActiveSession] = useState(null);
  const [userId, setUserId] = useState('user1'); // This should be dynamically set or fetched

  useEffect(() => {
    const fetchSessions = async () => {
      try {
        const response = await axios.get(`http://localhost:5500/api/conversations/sessions/${userId}`);
        setSessions(response.data);
      } catch (error) {
        console.error('Failed to fetch sessions:', error);
      }
    };

    fetchSessions();
  }, [userId]);

  const startNewSession = async () => {
    const sessionName = prompt('Enter session name:');
    if (!sessionName) return;

    try {
      const response = await axios.post('http://localhost:5500/api/messages/session', { userId, sessionName });
      setSessions([...sessions, response.data]);
      setActiveSession(response.data.sessionId);
    } catch (error) {
      console.error('Failed to start a new session:', error);
    }
  };

  return (
    <div className="App">
      <div className="sidebar">
        <h2>Chat Sessions</h2>
        <button onClick={startNewSession}>New Session</button>
        <ul>
          {sessions.map((session) => (
            <li key={session._id} onClick={() => setActiveSession(session._id)}>
              {session._id}
            </li>
          ))}
        </ul>
      </div>
      <div className="chat-container">
        <header className="chat-header">
          TRAILS ChatBot
        </header>
        {activeSession && <ChatBox sessionId={activeSession} />}
      </div>
    </div>
  );
}

export default App;
